SELECT TBLVENDAPISTAPROD.DATA,TBLVENDAPISTAPROD.QTDE,TANQUESBICOS.TANQUE,TANQUESBICOS.BICO,CADPRODUTOS.NOME As NomeProd,CADPRODUTOS.CODIGO FROM CADPRODUTOS INNER JOIN TBLVENDAPISTAPROD  ON CADPRODUTOS.IDPROD=TBLVENDAPISTAPROD.IDPROD LEFT OUTER JOIN TBLTIPOPRODUTO on CADPRODUTOS.TIPOPROD=TBLTIPOPRODUTO.IDTIPOPROD LEFT OUTER JOIN TANQUESBICOS ON TBLVENDAPISTAPROD.IDPROD=TANQUESBICOS.IDCOMB WHERE TBLTIPOPRODUTO.NOME='Combustiveis'
