SELECT TBLBICOS.BICO,TBLBICOS.VLRVENDA,TBLTANQUES.TANQUE,LOCALESTOCAGEM.NOME As NomeEstoque FROM TBLBICOS INNER JOIN TBLTANQUES ON TBLBICOS.IDTANQUEBICO=TBLTANQUES.IDTANQUES LEFT OUTER JOIN LOCALESTOCAGEM  ON  TBLBICOS.IDLOCAL=LOCALESTOCAGEM.IDESTOQUE WHERE TBLBICOS.BICO='5' AND TBLBICOS.IDTANQUEBICO='25'
