SELECT TBLVENDAPISTAPROD.QTDE,TBLVENDAPISTAPROD.VLRUNIT,TBLVENDAPISTAPROD.IDPROD,TBLVENDAPISTAPROD.IDPRODPISTA,TANQUESBICOS.TANQUE,TANQUESBICOS.BICO,CADPRODUTOS.NOME As NomeProd FROM TBLVENDAPISTAPROD INNER JOIN CADPRODUTOS ON TBLVENDAPISTAPROD.IDPROD=CADPRODUTOS.IDPROD INNER JOIN TANQUESBICOS ON TBLVENDAPISTAPROD.IDPROD=TANQUESBICOS.IDTANQUE INNER JOIN TBLTIPOPRODUTO ON CADPRODUTOS.TIPOPROD=TBLTIPOPRODUTO.IDTIPOPROD WHERE TBLTIPOPRODUTO.NOME = 'Combustiveis'
