SELECT TBLAFERICAO.AFERICAO,TBLAFERICAO.DATA,TBLAFERICAO.HORA,TBLTANQUES.TANQUE,TANQUESBICOS.BICO,CADPRODUTOS.NOME,CADPRODUTOS.CODBARRAS FROM TBLAFERICAO INNER JOIN TBLTANQUES ON TBLAFERICAO.IDBICO=TBLTANQUES.IDPROD INNER JOIN CADPRODUTOS ON TBLAFERICAO.IDBICO=CADPRODUTOS.IDPROD INNER JOIN TANQUESBICOS ON TBLAFERICAO.IDBICO=TANQUESBICOS.IDTANQUEBICO
