SELECT TBLCONTASAPAGAR.*,CADPRODUTOS.CODBARRAS,CADPRODUTOS.NOME As NomeProd,TBLHISTORICO.TIPO As TipoHistorico,TBLSERVICOSAPAGAR.TIPO As TipoServicos,TBLTIPODOCUMENTO.TIPO As Tipodoc,BANCOS.DESCRICAO As NomeBanco,TBLSITUACAO.DESCRICAO As TipoSituacao,TBLEVENTO.TIPO As TipoEvento,TBLCADASTRO.NOME As Fornecedor FROM TBLCONTASAPAGAR LEFT OUTER JOIN CADPRODUTOS ON TBLCONTASAPAGAR.IDPROD=CADPRODUTOS.IDPROD LEFT OUTER JOIN TBLHISTORICO ON  TBLCONTASAPAGAR.HISTORICO=TBLHISTORICO.IDHISTORICO LEFT OUTER JOIN TBLSERVICOSAPAGAR ON  TBLCONTASAPAGAR.IDSERVICOS=TBLSERVICOSAPAGAR.IDSERVICO LEFT OUTER JOIN TBLTIPODOCUMENTO ON  TBLCONTASAPAGAR.IDTIPODOC=TBLTIPODOCUMENTO.IDTIPODOC LEFT OUTER JOIN BANCOS ON  TBLCONTASAPAGAR.IDBANCO=BANCOS.IDBCO LEFT OUTER JOIN TBLSITUACAO ON  TBLCONTASAPAGAR.IDSITUACAO=TBLSITUACAO.IDSITUACAO   LEFT OUTER JOIN TBLEVENTO ON  TBLCONTASAPAGAR.IDEVENTO=TBLEVENTO.IDEVENTO LEFT OUTER JOIN TBLCADASTRO ON  TBLCONTASAPAGAR.IDFORNECEDOR=TBLCADASTRO.IDCAD
