SELECT TBLENTRADAESTOQUEPROD.*, CADPRODUTOS.NOME, LOCALESTOCAGEM.NOME As LocalEstoque FROM TBLENTRADAESTOQUEPROD LEFT OUTER JOIN CADPRODUTOS ON TBLENTRADAESTOQUEPROD.IDPROD = CADPRODUTOS.IDPROD LEFT OUTER JOIN LOCALESTOCAGEM ON CADPRODUTOS.IDLOCAL = LOCALESTOCAGEM.IDESTOQUE WHERE TBLENTRADAESTOQUEPROD.IDPROD=179
