SELECT TBLCADASTRO.NOME,TBLCADASTRO.CEP,TBLCADASTRO.NUMERO,TBLCADDOCUMENTACAO.CODIGO,TBLLOGRADOURO.ENDERECO,TBLLOGRADOURO.BAIRRO,TBLLOGRADOURO.CIDADE,TBLLOGRADOURO.ESTADO FROM TBLCADASTRO INNER JOIN TBLCADDOCUMENTACAO ON TBLCADASTRO.IDCAD=TBLCADDOCUMENTACAO.IDCAD  INNER JOIN TBLLOGRADOURO ON TBLCADASTRO.IDCAD=TBLLOGRADOURO.IDCAD
