SELECT TANQUESBICOS.BICO,TANQUESBICOS.TANQUE,TBLVENDAPISTAPROD.VLRVENDAA,TBLVENDAPISTAPROD.VLRUNIT,TBLVENDAPISTAPROD.QTDE,CADPRODUTOS.NOME As NomeProd,CADPRODUTOS.VALORVENDAA FROM TANQUESBICOS INNER JOIN TBLVENDAPISTAPROD ON TANQUESBICOS.IDTANQUE=TBLVENDAPISTAPROD.IDPROD INNER JOIN CADPRODUTOS ON TANQUESBICOS.IDTANQUE=CADPRODUTOS.IDPROD INNER JOIN TBLTIPOPRODUTO ON CADPRODUTOS.TIPOPROD=TBLTIPOPRODUTO.IDTIPOPROD WHERE TBLTIPOPRODUTO.NOME = 'Combustiveis'
