SELECT TBLAFERICAO.BICO,TBLAFERICAO.DATA,TBLAFERICAO.AFERICAO,CADPRODUTOS.NOME As NomeProd,TBLTANQUES.TANQUE FROM TBLAFERICAO INNER JOIN CADPRODUTOS ON TBLAFERICAO.IDPROD=CADPRODUTOS.IDPROD INNER JOIN TBLTANQUES ON TBLAFERICAO.IDPROD=TBLTANQUES.IDPROD INNER JOIN TBLTIPOPRODUTO ON CADPRODUTOS.IDPROD=TBLTIPOPRODUTO.IDTIPOPROD WHERE TBLTIPOPRODUTO.NOME='Combustiveis'
